document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("theme-toggle"),t=document.documentElement;if(!e)return;function n(){const o=t.getAttribute("data-theme")==="dark",n=e.querySelector(".moon-icon"),s=e.querySelector(".sun-icon");o?(n.classList.add("hidden"),s.classList.remove("hidden")):(n.classList.remove("hidden"),s.classList.add("hidden"))}n(),e.addEventListener("click",function(){const s=t.getAttribute("data-theme")==="dark",e=s?"light":"dark";t.setAttribute("data-theme",e),localStorage.setItem("theme",e),n()})}),document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".post-content h2, .post-content h3, .post-content h4").forEach(e=>{e.id&&(e.style.cursor="pointer",e.addEventListener("click",()=>{window.location.hash=e.id}))})})